import { HATS, JACKETS, SNEAKERS, WOMENS, MENS } from "../../constants/routes";

export const SECTION_TITLES = {
  hats: "hats",
  jackets: "jackets",
  sneakers: "sneakers",
  womens: "womens",
  mens: "mens",
};

const INITIAL_STATE = {
  sections: [
    {
      title: "hats",
      imageUrl: "https://i.ibb.co/cvpntL1/hats.png",
      id: 1,
      linkUrl: HATS,
    },
    {
      title: "jackets",
      imageUrl:
        "https://i.ibb.co/PjmxCzr/isaac-ramirez-UNSj7-Bt-OK0-Q-unsplash.jpg",
      id: 2,
      linkUrl: JACKETS,
    },
    {
      title: "sneakers",
      imageUrl: "https://i.ibb.co/0jqHpnp/sneakers.png",
      id: 3,
      linkUrl: SNEAKERS,
    },
    {
      title: "womens",
      imageUrl:
        "https://i.ibb.co/gv0g3q2/ian-dooley-TT-ROx-Wj9n-A-unsplash.jpg",
      size: "large",
      id: 4,
      linkUrl: WOMENS,
    },
    {
      title: "mens",
      imageUrl: "https://i.ibb.co/R70vBrQ/men.png",
      size: "large",
      id: 5,
      linkUrl: MENS,
    },
  ],
};

const directoryReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default directoryReducer;
