import React from "react";
import CollectionItem from "../CollectionItem/CollectionItem";
import {
  CollectionPreviewContainer,
  TitleContainer,
  PreviewContainer,
} from "./CollectionPreviewStyles";
import { HATS, JACKETS, SNEAKERS, WOMENS, MENS } from "../../constants/routes";

const COLLECTION_PREVIEW_MAP = {
  Hats: HATS,
  Sneakers: JACKETS,
  Jackets: SNEAKERS,
  Womens: WOMENS,
  Mens: MENS,
};

const CollectionPreview = ({ title, items }) => {
  console.log("title", title);
  return (
    <CollectionPreviewContainer>
      <TitleContainer component="h1">{title.toUpperCase()}</TitleContainer>
      <PreviewContainer>
        {items
          .filter((item, index) => index < 4)
          .map((item) => (
            <CollectionItem key={item.id} item={item} />
          ))}
      </PreviewContainer>
    </CollectionPreviewContainer>
  );
};

export default CollectionPreview;
